import React from 'react'
import PropTypes from 'prop-types'
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import * as LoginActions from 'store/modules/login';
import Login from 'components/login/Login';
import { withRouter } from 'react-router-dom'

class LoginContainer extends React.Component {
  handleLogin = async () =>{

    const { LoginActions, id, password, history }= this.props;
    console.log(id, password);
    try{
      await LoginActions.login({id, password});
      history.push('/')
    }catch(e){
      console.log(e);
    }
  }
  handleIdChange = (e) => {
    const { value } = e.target;
    const { LoginActions } = this.props;
    LoginActions.changeIdInput(value);
  }
  handlePwChange = (e) => {
    const { value } = e.target;
    const { LoginActions } = this.props;
    LoginActions.changePasswordInput(value);
  }
  handleKeyPress = (e) => {
    if(e.key === 'enter'){
      this.handleLogin();
    }
  }

  render () {
    const {
      handleLogin, handleIdChange, handlePwChange, handleKeyPress
    } = this;
    const { error, password, id} = this.props;
    return (
      <Login
        onLogin={handleLogin} onIdChange={handleIdChange} onPwChange={handlePwChange} onKeyPress={handleKeyPress} id={id} password={password}
      />
    )
  }
}

export default connect(
  (state) => ({
    id : state.login.getIn(['loginBox', 'id']),
    password : state.login.getIn(['loginBox', 'password']),
    error : state.login.getIn(['loginBox', 'error'])
  }),
  (dispatch) => ({
    LoginActions : bindActionCreators(LoginActions, dispatch)
  })
)(withRouter(LoginContainer));
